mapper.select:
SELECT 
    *
FROM 
    AF_FIXED_ASSET
WHERE 
    ACQU_DATE BETWEEN #{acquStartDate} AND #{acquEndDate}


        ASSET_CD = #{assetCd}


        AND EQUIP_CD = #{equipCd}


        AND MNGNT_CD = #{mngntCd}


        AND DEPRE_MTHD = #{depreMthd}


        AND OWN_DEPT = #{ownDept}


ORDER BY 
    ASSET_NO

mapper.update:
UPDATE 
    AF_FIXED_ASSET 
SET
    ASSET_CD=#{assetCd}
   ,EQUIP_CD=#{equipCd}
   ,MNGNT_CD=#{mngntCd}
   ,ACQU_FINAN_CD=#{acquFinanCd}
   ,ACQU_DATE=#{acquDate}
   ,DPRO_DATE=#{dproDate}
   ,IN_OUT_CAPITAL_CD=#{inOutCapitalCd}
   ,ACNT_CD=#{acntCd}
   ,ACQU_CD=#{acquCd}
   ,PROFIT_CD=#{profitCd}
   ,ASSET_NM=#{assetNm}
   ,ACNT=#{acnt}
   ,ASST_ACNT=#{asstAcnt}
   ,ASSET_GRP=#{assetGrp}
   ,DISPOSAL_DATE=#{disposalDate}
   ,DISPOSAL_CD=#{disposalCd}
   ,ACQU_AMT=#{acquAmt}
   ,MON_UNIT_CD=#{monUnitCd}
   ,SPLY_COMP=#{splyComp}
   ,CASH_BUY_YN=#{cashBuyYn}
   ,PROD_COMP=#{prodComp}
   ,MODEL_NM=#{modelNm}
   ,PROD_NO=#{prodNo}
   ,PROD_CTRY=#{prodCtry}
   ,RECEIVER=#{receiver}
   ,OWN_DEPT=#{ownDept}
   ,PST_LOC=#{pstLoc}
   ,RESPO_WORKER=#{respoWorker}
   ,GRADE=#{grade}
   ,FIXED_ASSET_PHOTO=#{fixedAssetPhoto}
   ,DEPRE_MTHD=#{depreMthd}
   ,DEPRE_YEARS=#{depreYears}
   ,DEPRE_RATE=#{depreRate}
WHERE 
    ASSET_NO=#{assetNo}

   UPDATE AF_FIXED_ASSET SET
	 DISPOSAL_CD=#{disposalCd}
	,DISPOSAL_DATE=#{disposalDate}
	WHERE ASSET_NO=#{assetNo}

UPDATE AF_FIXED_ASSET SET
MNGNT_CD=#{mngntCd}
,OWN_DEPT=#{ownDept}
,PST_LOC=#{pstLoc}
,RESPO_WORKER=#{respoWorker}
WHERE ASSET_NO=#{assetNo}
