mapper.select:
SELECT RESOL_NO ,RESOL_DIV ,RESOL_DATE ,RESOLER ,RESOL_DEPT_CD ,PROG_STAT_DIV ,SAK_NO ,RECEIPT_NO ,CUST_CD ,OPER_NO ,RESOL_AMT ,BR_CAU ,ABS ,DEMD_DEPT_CD ,AUTO_JOUR_YB ,MOD_WORKER ,MOD_DATE ,DAN_DIV ,ACNT_CD ,ASSI_SUB_CD ,CTRT_NO ,SURTAX_YB ,RESOL_DEPT_EXTSN ,CLOS_YB ,ACC_PRID ,RESOLER_NM ,ACNT_CD_NM ,ASSI_SUB_CD_NM FROM AR_RESOL_LET WHERE 1=1
AND RESOL_NO = #{resolNo}
AND ACC_PRID = #{accPrid}
AND RESOL_DIV = #{resolDiv}
AND to_number(RESOL_DATE) >= #{startDate} AND to_number(RESOL_DATE) <= #{endDate}
AND to_number(RESOL_NO) >= #{startResolNo} AND to_number(RESOL_NO) <= #{endResolNo}
AND RESOL_DEPT_CD = #{resolDeptCd}
AND RESOL_DIV IN ('1','2') AND PROG_STAT_DIV = '36'
AND PROG_STAT_DIV IN ('89','53')
ORDER BY RESOL_NO DESC

mapper.update:
UPDATE 
    AR_RESOL_LET 
SET  
    RESOL_DIV        = #{resolDiv      }
   ,RESOL_DATE       = #{resolDate     }
   ,RESOLER          = #{resoler       }
   ,RESOL_DEPT_CD    = #{resolDeptCd   }
   ,PROG_STAT_DIV    = #{progStatDiv   }
   ,SAK_NO           = #{sakNo         }
   ,RECEIPT_NO       = #{receiptNo     }
   ,CUST_CD          = #{custCd        }
   ,OPER_NO          = #{operNo        }
   ,RESOL_AMT        = #{resolAmt      }
   ,BR_CAU           = #{brCau         }
   ,ABS              = #{abs           }
   ,DEMD_DEPT_CD     = #{demdDeptCd    }
   ,AUTO_JOUR_YB     = #{autoJourYb    }
   ,MOD_WORKER       = #{modWorker     }
   ,MOD_DATE         = #{modDate       }
   ,DAN_DIV          = #{danDiv        }
   ,ACNT_CD          = #{acntCd        }
   ,ASSI_SUB_CD      = #{assiSubCd     }
   ,CTRT_NO          = #{ctrtNo        }
   ,SURTAX_YB        = #{surtaxYb      }
   ,RESOL_DEPT_EXTSN = #{resolDeptExtsn}
   ,CLOS_YB          = #{closYb        }
   ,ACC_PRID         = #{accPrid       }
   ,RESOLER_NM       = #{resolerNm     }
   ,ACNT_CD_NM       = #{acntCdNm      }
   ,ASSI_SUB_CD_NM   = #{assiSubCdNm   }
WHERE
    RESOL_NO         = #{resolNo       }
