mapper.select:
SELECT DISTINCT o.TRMT_DEPT as trmtDept, o.TRMT_DOCT as trmtDoct, o.PAT_NO as patNo, p.PAT_NM as patNm, p.AGE as age, p.ADDR as addr, p.RRN1 as rrn1, p.GENDER as gender, h.DSCG_DATE as dscgDate, h.HOSPTLZ_DATE as hosptlzDate, h.WARD as ward, h.HROOM as hroom FROM WS_OUTPA_RECEIPT o, WS_PAT p, WH_HOSPTLZ_RECEIPT h, (SELECT MAX(OUTPA_RECEIPT_NO) OUTPA_RECEIPT_NO, PAT_NO FROM WS_OUTPA_RECEIPT WHERE HOSPTLZ_YB='Y' GROUP BY PAT_NO ) maxO WHERE o.OUTPA_RECEIPT_NO = MAXO.OUTPA_RECEIPT_NO AND o.PAT_NO= p.PAT_NO AND h.PAT_NO=o.PAT_NO
AND p.PAT_NM = #{patNm}
AND p.PAT_NO = #{patNo}
AND h.HOSPTLZ_DATE BETWEEN #{start} AND #{end}
AND h.WARD = #{ward}
AND o.TRMT_DEPT = #{deptCd}
AND o.TRMT_DOCT =#{respDoctNm}
