mapper.select:
SELECT DEPT_CD as deptCd, EMP_NO as empNo, EMP_NM as empNm, JOBTT, JOBCL, JOBRK, PAY_STEP as payStep, HIRE_DATE as hireDate, WORK_STATUS as workStatus, DEPT_NM as deptNm FROM HM_EMP
AND RET_DATE is null
AND DEPT_CD = #{deptCd}
AND DEPT_CD = #{deptCode}
AND EMP_NO = #{empNo}
AND EMP_NM = #{empNm}

mapper.insert:
		INSERT INTO
		  HA_APPMT 
          (
              APPMT_SEQ,
              APPMT_SUBJECT,
              APPMT_DIV
              ,APPMT_DATE
              ,TRANS_DEPT
              ,EMP_NO
              ,TRANS_DEPT_NM
              ,EMP_NM
              ,APPMTYN
          )
		  VALUES
		  (
              #{appmtSeq}
              ,#{appmtSubject}
              ,#{appmtDiv}
              ,#{appmtDate}
              ,#{transDept}
              ,#{empNo}
              ,#{transDeptNm}
              ,#{empNm}
              ,#{appmtyn}
          )

mapper.select:

BEGIN
    DELETE FROM HA_APPMT_DETAIL 

    WHERE
          EMP_NO = #{empNo}
    AND
          APPMT_SEQ = #{appmtSeq};
    DELETE FROM HA_APPMT

    WHERE
          EMP_NO = #{empNo}
    AND
          APPMT_SEQ = #{appmtSeq}; 
END;

select a.EMP_NO as empNo, a.EMP_NM as empNm, a.APPMT_SUBJECT as appmtSubject, a.APPMT_DATE as appmtDate, b.APPMT_DEPT_NM as appmtDeptNm, b.APPMT_JOBCL_NM as appmtJobclNm, b.APPMT_JOBRK_NM as appmtJobrkNm, b.APPMT_JOBTT as appmtJobtt, b.APPMT_PSTEP as appmtPstep, b.YY_PSTEP as yyPstep, b.PAY as pay, b.REASON as reason, b.NOTE as note, b.BASE_APPMT_NM as baseAppmtNm from HA_APPMT a,HA_APPMT_DETAIL b WHERE a.EMP_NO=b.EMP_NO AND a.APPMT_SEQ=b.APPMT_SEQ
AND b.CURR_DEPT = #{deptCode}
AND a.EMP_NO = #{empNo}
AND a.EMP_NM = #{empNm}
AND a.APPMT_DATE BETWEEN #{startDay} and #{endDay}

mapper.delete:
DELETE FROM HA_APPMT_DETAIL 

       WHERE
             EMP_NO = #{empNo}
       AND
             APPMT_SEQ = #{appmtSeq}

mapper.select:
		BEGIN
        UPDATE
		  HM_EMP
		SET
		JOBTT =#{appmtJobtt}
		,JOBCL = #{appmtJobclNm}
		,JOBRK = #{appmtJobrkNm}
		,DEPT_CD = #{appmtDept}
		,DEPT_NM = #{appmtDeptNm}

		,PAY_STEP=#{appmtPstep}

		where
		 EMP_NO =#{empNo};

         DELETE FROM HA_APPMT_DETAIL 

         WHERE
               EMP_NO = #{empNo}
         AND
               APPMT_SEQ = #{appmtSeq};
         DELETE FROM HA_APPMT

         WHERE
               EMP_NO = #{empNo}
         AND
                APPMT_SEQ = #{appmtSeq}; 
       END;

BEGIN
UPDATE HM_EMP 
SET
    WORK_STATUS = #{workStatus}
   ,RET_DATE = #{retDate}
WHERE
     EMP_NO = #{empNo};
DELETE 
    FROM HA_APPMT
WHERE
    EMP_NO = #{empNo}
AND
    APPMT_SEQ = #{appmtSeq};
END;
