mapper.select:
select IN_OUT_TIME , REASON , INOUT_GUBUN , INOUT_TIME_NO , START_DATE , END_DATE , EMP_NO , EMP_NM , OVER_TIME , BELONG_DAY from HG_INOUT_TIME where 1=1
and EMP_NO = #{empNo} and BELONG_DAY BETWEEN #{sDate} and #{eDate}
order by INOUT_TIME_NO

mapper.update:
INSERT INTO HG_INOUT_TIME
(
  IN_OUT_TIME
, REASON
, INOUT_GUBUN
, INOUT_TIME_NO
, START_DATE
, END_DATE
, EMP_NO
, EMP_NM
, OVER_TIME
, BELONG_DAY
)
VALUES
(
  #{inOutTime}
, #{reason}
, #{inoutGubun}
, #{inoutTimeNo}
, #{startDate}
, #{endDate}
, #{empNo}
, #{empNm}
, #{overTime}
, #{belongDay}
)

		UPDATE HG_INOUT_TIME SET
			IN_OUT_TIME = #{inOutTime}
		  , BELONG_DAY = #{belongDay}
		  , REASON = #{reason}
		  , INOUT_GUBUN = #{inoutGubun}
          , OVER_TIME = #{overTime}
		WHERE INOUT_TIME_NO = #{inoutTimeNo}
