mapper.select:
SELECT BIG_GRP_CD AS code , BIG_GRP_NM AS value FROM LC_BIG_GRP_CD
AND BIG_GRP_CD = #{code}
ORDER BY BIG_GRP_CD

SELECT MID_GRP_CD AS code , MID_GRP_CD_NM AS value FROM LC_MID_GRP_CD
AND MID_GRP_CD = #{code}
AND BIG_GRP_CD = #{secCode}
ORDER BY MID_GRP_CD

SELECT 
    SM_GRP_CD AS code
  , SM_GRP_CD_NM AS value
FROM
    LC_SM_GRP_CD


        AND SM_GRP_CD = #{code}


        AND MID_GRP_CD = #{secCode}


        AND BIG_GRP_CD = #{thrCode}


ORDER BY
    SM_GRP_CD

mapper.insert:
INSERT INTO
    LC_BIG_GRP_CD (
        USE_YB
      , REG_DATE
      , MOD_WORKER_ID
      , REG_WORKER_ID
      , GRP_DIV
      , MOD_DATE
      , REG_WORKER_IP
      , MOD_WORKER_IP
      , BIG_GRP_NM
      , BIG_GRP_CD
      , USER_REG_POSS_YB
   )
VALUES (
    #{useYb}
  , #{regDate}
  , #{modWorkerId}
  , #{regWorkerId}
  , #{grpDiv}
  , #{modDate}
  , #{regWorkerIp}
  , #{modWorkerIp}
  , #{bigGrpNm}
  , #{bigGrpCd}
  , #{userRegPossYb}
)

mapper.update:
UPDATE
    LC_BIG_GRP_CD
SET
    USE_YB           = #{useYb}
  , REG_DATE         = #{regDate}
  , MOD_WORKER_ID    = #{modWorkerId}
  , REG_WORKER_ID    = #{regWorkerId}
  , GRP_DIV          = #{grpDiv}
  , MOD_DATE         = #{modDate}
  , REG_WORKER_IP    = #{regWorkerIp}
  , MOD_WORKER_IP    = #{modWorkerIp}
  , BIG_GRP_NM       = #{bigGrpNm}
  , BIG_GRP_CD       = #{bigGrpCd}
  , USER_REG_POSS_YB = #{userRegPossYb}
WHERE
    BIG_GRP_CD       = #{bigGrpCd}

mapper.insert:
INSERT INTO
    LC_MID_GRP_CD (
        BIG_GRP_CD
      , USER_REG_POSS_YB
      , MID_GRP_CD
      , USE_YB
      , REG_WORKER_IP
      , REG_WORKER_ID
      , REG_DATE
      , MID_GRP_CD_NM
      , MOD_WORKER_IP
      , MOD_DATE
      , MOD_WORKER_ID
    )
VALUES (
    #{bigGrpCd}
  , #{userRegPossYb}
  , #{midGrpCd}
  , #{useYb}
  , #{regWorkerIp}
  , #{regWorkerId}
  , #{regDate}
  , #{midGrpCdNm}
  , #{modWorkerIp}
  , #{modDate}
  , #{modWorkerId}
)

mapper.update:
UPDATE
    LC_MID_GRP_CD
SET
    BIG_GRP_CD          = #{bigGrpCd}
  , USER_REG_POSS_YB    = #{userRegPossYb}
  , MID_GRP_CD          = #{midGrpCd}
  , USE_YB              = #{useYb}
  , REG_WORKER_IP       = #{regWorkerIp}
  , REG_WORKER_ID       = #{regWorkerId}
  , REG_DATE            = #{regDate}
  , MID_GRP_CD_NM       = #{midGrpCdNm}
  , MOD_WORKER_IP       = #{modWorkerIp}
  , MOD_DATE            = #{modDate}
  , MOD_WORKER_ID       = #{modWorkerId}
WHERE
    BIG_GRP_CD          = #{bigGrpCd}
AND MID_GRP_CD          = #{midGrpCd}

mapper.insert:
INSERT INTO
    LC_SM_GRP_CD (
        REG_DATE
      , MID_GRP_CD
      , SM_GRP_CD_NM
      , REG_WORKER_IP
      , USE_YB
      , MOD_WORKER_ID
      , BIG_GRP_CD
      , USER_REG_POSS_YB
      , MOD_DATE
      , MOD_WORKER_IP
      , SM_GRP_CD
      , REG_WORKER_ID
)
VALUES (
    #{regDate}
  , #{midGrpCd}
  , #{smGrpCdNm}
  , #{regWorkerIp}
  , #{useYb}
  , #{modWorkerId}
  , #{bigGrpCd}
  , #{userRegPossYb}
  , #{modDate}
  , #{modWorkerIp}
  , #{smGrpCd}
  , #{regWorkerId}
)

mapper.update:
UPDATE
    LC_SM_GRP_CD
SET
    REG_DATE        = #{regDate}
 , MID_GRP_CD       = #{midGrpCd}
 , SM_GRP_CD_NM     = #{smGrpCdNm}
 , REG_WORKER_IP    = #{regWorkerIp}
 , USE_YB           = #{useYb}
 , MOD_WORKER_ID    = #{modWorkerId}
 , BIG_GRP_CD       = #{bigGrpCd}
 , USER_REG_POSS_YB = #{userRegPossYb}
 , MOD_DATE         = #{modDate}
 , MOD_WORKER_IP    = #{modWorkerIp}
 , SM_GRP_CD        = #{smGrpCd}
 , REG_WORKER_ID    = #{regWorkerId}
WHERE
    MID_GRP_CD      = #{midGrpCd}
AND BIG_GRP_CD      = #{bigGrpCd}
AND SM_GRP_CD       = #{smGrpCd}

mapper.delete:
DELETE FROM
    LC_MID_GRP_CD
WHERE
    BIG_GRP_CD      = #{bigGrpCd}
AND MID_GRP_CD      = #{midGrpCd}

DELETE FROM
    LC_SM_GRP_CD
WHERE
    BIG_GRP_CD      = #{bigGrpCd}
AND MID_GRP_CD      = #{midGrpCd}
AND SM_GRP_CD       = #{smGrpCd}
