mapper.select:
SELECT
    *
FROM
    MC_TRANSFER_PATIENT



        AND PAT_NO      = #{patNo}


        AND ADVICE_RESULT = #{adviceResult}


        AND TRANSFER_DATE BETWEEN = #{transStartDate} AND #{transEndDate}

mapper.update:
        INSERT INTO
            MC_TRANSFER_PATIENT(
                TRANSFER_NO
				, PAT_NO
				, RECEIPT_DATE
				, RECODE_DATE
				, TRANSFER_DATE
				, TRMT_DEPT
				, TRMT_DOCT
				, DGNS_NM
				, DISCHARGE_DATE
				, TRANSFER_HOS_NM
				, TRANSFER_HOS_DEPT
				, TRANSFER_HOS_DOCT
				, TRANSFER_HOS_TEL
				, TRANSFER_REASON
				, PROVIDING_MED_INFO
				, RECODE_YN
				, ADVICE_RESULT
				, NOTE
            )
        VALUES (
            #{transferNo}
          , #{patNo}
          , #{receiptDate}
          , #{recodeDate}
          , #{transferDate}
          , #{trmtDept}
          , #{trmtDoct}
          , #{dgnsNm}
          , #{dischargeDate}
          , #{transferHosNm}
          , #{transferHosDept}
          , #{transferHosDoct}
          , #{transferHosTel}
          , #{transferReason}
          , #{providingMedInfo}
          , 'Y'
          , #{adviceResult}
          , #{note}
        )

UPDATE
    MC_TRANSFER_PATIENT
SET
    TRANSFER_NO       = #{transferNo}
    , RECEIPT_DATE       = #{receiptDate}
    , RECODE_DATE       = #{recodeDate}
    , TRANSFER_DATE       = #{transferDate}
    , TRMT_DEPT       = #{trmtDept}
    , TRMT_DOCT       = #{trmtDoct}
    , DGNS_NM       = #{dgnsNm}
    , DISCHARGE_DATE       = #{dischargeDate}
    , TRANSFER_HOS_NM       = #{transferHosNm}
    , TRANSFER_HOS_DEPT       = #{transferHosDept}
    , TRANSFER_HOS_DOCT       = #{transferHosDoct}
    , TRANSFER_HOS_TEL       = #{transferHosTel}
    , TRANSFER_REASON       = #{transferReason}
    , PROVIDING_MED_INFO       = #{providingMedInfo}
    , RECODE_YN       = 'Y'
    , ADVICE_RESULT       = #{adviceResult}
    , NOTE       = #{note}
WHERE
    PAT_NO           = #{patNo}
