mapper.delete:
DELETE 
  FROM AB_BUDG_BIMOK 
 WHERE ACC_YEAR = #{accYear} 
       AND BIMOK_CD = #{bimokCd}

mapper.select:
     	{call PC_AB_COPY_BIMOK (
	      		#{accYear, mode=IN, jdbcType=VARCHAR}
				, #{errorCode, mode=OUT, jdbcType=VARCHAR}
		      	, #{errorMsg, mode=OUT, jdbcType=VARCHAR}
     	) }
