mapper.update:
UPDATE 
    AE_ASSI_TYPE SET
        ASSI_TYPE_CD    =   #{assiTypeCd}, 
        ASSI_TYPE_NM  =   #{assiTypeNm}, 
        USE_YN        =   #{useYn     }, 
        ACC_YEAR      =   #{accYear   }
WHERE 
    ASSI_TYPE_CD=#{assiTypeCd }
    AND ACC_YEAR=#{accYear}

mapper.delete:
DELETE 
    AE_ASSI_TYPE
WHERE 
    ASSI_TYPE_CD=#{assiTypeCd }
    AND ACC_YEAR=#{accYear}

mapper.select:
{call PC_AE_COPY_ASSI (
       #{accYear, mode=IN, jdbcType=VARCHAR}, 
       #{errorCode, mode=OUT, jdbcType=VARCHAR}, 
       #{errorMsg, mode=OUT, jdbcType=VARCHAR}
) }
