mapper.select:
SELECT DETAIL_CODE AS detailCode , EDIT_IP AS editIp , EDIT_ID AS editId , USE_YN AS useYn , MODIYN AS modiyn , REG_IP AS regIp , REG_ID AS regId , CODE AS code , SUB_CODE AS subCode , CODE_NAME AS codeName , DETAIL_CODE_NAME AS detailCodeName FROM CM_CODE WHERE SUB_CODE = #{subCode}
AND CODE = #{code}
AND CODE_NAME LIKE '%' || #{codeName} || '%'
AND USE_YN = #{use}
ORDER BY DETAIL_CODE

mapper.delete:
DELETE FROM
    CM_CODE
WHERE
    CODE        = #{code}
AND DETAIL_CODE = #{detailCode}
