mapper.select:
select START_OVERTIME , END_OVERTIME , TOTAL_OVERTIME , OVER_STATUS , REASON , APPROVAL_REASON , OVERTIME_NO , EMP_NO , EMP_NM , DEPT_CD , DEPT_NM , START_OVERTIME_DATE , END_OVERTIME_DATE , BEFORE_OVERTIME_NO from HG_OVERTIME_WORK where 1=1
and EMP_NO = #{empNo} AND START_OVERTIME_DATE BETWEEN #{sDate} AND #{eDate}
order by OVERTIME_NO

mapper.update:
update HG_OVERTIME_WORK
set
	START_OVERTIME = #{startOvertime}
  , END_OVERTIME = #{endOvertime}
  , TOTAL_OVERTIME = #{totalOvertime}
  , OVER_STATUS = #{overStatus}
  , REASON = #{reason}
  , APPROVAL_REASON = #{approvalReason}
  , EMP_NO = #{empNo}
  , EMP_NM = #{empNm}
  , DEPT_CD = #{deptCd}
  , DEPT_NM = #{deptNm}
  , START_OVERTIME_DATE = #{startOvertimeDate}
  , END_OVERTIME_DATE = #{endOvertimeDate}
  , BEFORE_OVERTIME_NO = #{beforeOvertimeNo}
where OVERTIME_NO = #{overtimeNo}
