mapper.update:
	UPDATE HS_PAYDAY 
	SET
		payment_Date  = #{paymentDate}
    WHERE belong_Ym   = #{belongYm}
    	AND job_Class = #{jobClass}

mapper.delete:
DELETE HS_PAYDAY
WHERE belong_Ym = #{belongYm}
	AND job_Class =#{jobClass}
	AND payment_Date =#{paymentDate}

DELETE HS_REGULAR_WORKS_SALARY
WHERE belong_Ym = #{belongYm}
	AND job_Class =#{jobClass}

mapper.select:
     SELECT DLINE,
     	BELONG_YM as belongYm,
     	JOB_CLASS as jobClass
     FROM HS_REGULAR_WORKS_SALARY
	 WHERE BELONG_YM = #{belongYm}
	 	AND JOB_CLASS = #{jobClass}
	 	AND PAYMENT_DATE = #{paymentDate}
	 	AND DLINE = 1
