      , REG_DATE
      , MID_GRP_CD
      , VMI_YB
      , MOD_WORKER_ID
      , EDI_CD
      , ORD_UNIT
      , CTRT_PRICE
      , STAND
      , GDS_CD
      , BIG_GRP_CD
      , SUBUL_UNIT
      , REG_WORKER_IP
      , DISUSE_DATE
      , MOD_DATE
      , SPLIES_DIV
      , MOD_WORKER_IP
      , CTRT_START_DATE
      , RACK_NO
      , PIC_LOC
      , GDS_NM
      , REG_WORKER_ID
      , WH
      , USE_DEPT
      , NOTE
      , CTRT_END_DATE
    )
VALUES (
    #{smGrpCd}
  , #{disuseDiv}
  , #{cngQnt}
  , #{insurDiv}
  , #{cust}
  , #{regDate}
  , #{midGrpCd}
  , #{vmiYb}
  , #{modWorkerId}
  , #{ediCd}
  , #{ordUnit}
  , #{ctrtPrice}
  , #{stand}
  , #{gdsCd}
  , #{bigGrpCd}
  , #{subulUnit}
  , #{regWorkerIp}
  , #{disuseDate}
  , #{modDate}
  , #{spliesDiv}
  , #{modWorkerIp}
  , #{ctrtStartDate}
  , #{rackNo}
  , #{picLoc}
  , #{gdsNm}
  , #{regWorkerId}
  , #{wh}
  , #{useDept}
  , #{note}
  , #{ctrtEndDate}
)

mapper.update:
UPDATE 
    LC_GDS
SET
    SM_GRP_CD       = #{smGrpCd}
  , DISUSE_DIV      = #{disuseDiv}
  , CNG_QNT         = #{cngQnt}
  , INSUR_DIV       = #{insurDiv}
  , CUST            = #{cust}
  , REG_DATE        = #{regDate}
  , MID_GRP_CD      = #{midGrpCd}
  , VMI_YB          = #{vmiYb}
  , MOD_WORKER_ID   = #{modWorkerId}
  , EDI_CD          = #{ediCd}
  , ORD_UNIT        = #{ordUnit}
  , CTRT_PRICE      = #{ctrtPrice}
  , STAND           = #{stand}
  , GDS_CD          = #{gdsCd}
  , BIG_GRP_CD      = #{bigGrpCd}
  , SUBUL_UNIT      = #{subulUnit}
  , REG_WORKER_IP   = #{regWorkerIp}
  , DISUSE_DATE     = #{disuseDate}
  , MOD_DATE        = #{modDate}
  , SPLIES_DIV      = #{spliesDiv}
  , MOD_WORKER_IP   = #{modWorkerIp}
  , CTRT_START_DATE = #{ctrtStartDate}
  , RACK_NO         = #{rackNo}
  , PIC_LOC         = #{picLoc}
  , GDS_NM          = #{gdsNm}
  , REG_WORKER_ID   = #{regWorkerId}
  , WH              = #{wh}
  , USE_DEPT        = #{useDept}
  , NOTE            = #{note}
  , CTRT_END_DATE   = #{ctrtEndDate}
WHERE
    SM_GRP_CD = #{smGrpCd}
AND MID_GRP_CD = #{midGrpCd}
AND BIG_GRP_CD = #{bigGrpCd}
AND GDS_CD = #{gdsCd}
