mapper.select:
	    SELECT
	    *
	      FROM LS_PST_INVEN


           AND WH_CD = #{whCd}


		   AND GDS_CD = #{gdsCd}

mapper.insert:
	    INSERT INTO LS_PST_INVEN(
	           		WH_CD
                  , GDS_CD
                  , MID_GRP_CD
                  , SM_GRP_CD
                  , PST_INVEN_QNT
                  , BIG_GRP_CD
                  , IO_CFMT_CD
                  , IO_TYPE_CD
                  , GDS_NM
                  , UNIT
                  , USE_DEPT
	    )VALUES(#{whCd}
                  , #{gdsCd}
                  , #{midGrpCd}
                  , #{smGrpCd}
                  , #{pstInvenQnt}
                  , #{bigGrpCd}
                  , '1'
                  , '02'
                  , #{gdsNm}
                  , #{unit}
                  , #{useDept}
                  )

mapper.update:
	    UPDATE LS_PST_INVEN SET
			  PST_INVEN_QNT=#{pstInvenQnt}
			  USE_DEPT=#{useDept}
	           PST_INVEN_QNT=#{pstInvenQnt}
             WHERE WH_CD=#{whCd}
               AND SM_GRP_CD=#{smGrpCd}
               AND MID_GRP_CD=#{midGrpCd}
               AND BIG_GRP_CD=#{bigGrpCd}
               AND GDS_CD=#{gdsCd}

mapper.delete:
	 DELETE FROM LS_PST_INVEN
	   WHERE WH_CD=#{whCd}
               AND SM_GRP_CD=#{smGrpCd}
               AND MID_GRP_CD=#{midGrpCd}
               AND BIG_GRP_CD=#{bigGrpCd}
               AND GDS_CD=#{gdsCd}
