mapper.update:
UPDATE SL_CLINSPE_SLI
       SET CLINSPE_NO=#{clinspeNo},
       CLINSPE_BLOK_CODE=#{clinspeBlokCode},
       SLI_CODE=#{sliCode},
       SLI_PROD_DAY=#{sliProdDay},
       SLI_QNT=#{sliQnt},
       SLI_CON=#{sliCon}
 WHERE CLINSPE_NO=#{clinspeNo}
       AND SLI_CODE=#{sliCode}

mapper.delete:
DELETE
  FROM SL_CLINSPE_SLI
 WHERE CLINSPE_NO=#{clinspeNo}
       AND SLI_CODE = #{sliCode}
